import React ,{useState} from 'react'
import Base from '../core/Base';
import {Link} from 'react-router-dom';
import {signup} from '../auth/helper';

const Signup = () =>{

    const [values,setValues] = useState({
        name:'',
        email:'',
        password:'',
        error:'',
        success:false
    });

    const {name,email,password,error,success} = values;

    const handleChnage = name => event =>{
        setValues({ ...values,error:false,[name]:event.target.value})
    }

    const onSubmit = event =>{
        event.preventDefault()
        setValues({...values,error:false})
        signup({name,email,password})
        .then(data=>{
            if(data.error){
                setValues({...values,error:data.error,success})
            }
        })
        .catch()
    }

    const signupForm =() =>{
        return(
            <div className='row'>
                <div className='col-md-6 offset-sm-3 text-left'>
                    <form>
                        <div className='form-group'>
                            <label className='text-light'>Name</label>
                            <input className='form-control' 
                            onChange={handleChnage('name')}
                            type='text'/>
                        </div>
                        <div className='form-group'>
                            <label className='text-light'>Email</label>
                            <input className='form-control'
                            onChange={handleChnage('email')}
                            type='email'/>
                        </div>
                        <div className='form-group'>
                            <label className='text-light'>password</label>
                            <input className='form-control' 
                            onChange={handleChnage('password')}
                            type='password'/>
                        </div>
                        <button className='btn btn-success btn-block'>submit</button>
                    </form>
                </div>
            </div>
        ) 
    }

    return(
        <Base title='Sign up page' description='Sign up Page for User!'>
            {signupForm()}
        </Base>
    )
};

export default signup;
