import { createPlayer } from '../core/helper/coreapicalls';
import React, { useState, useEffect } from "react";
import Base from "../core/Base";
import { Link } from "react-router-dom";
import { ChangePassword } from "../admin/helper/adminapicall";
import { isAuthenticated } from "../auth/helper/index";

const Password = () => {
  // https://pubg-mobile-tournament.herokuapp.com/api
  const { user, token } = isAuthenticated();

  const [values, setValues] = useState({
    oldpass: "",
    newpass: "",
    confirm: "",
    loading: false,
    error: "",
    createdProduct:"",
    getaRedirect: false,
    formData: ""
  });

  const {
    oldpass,
    newpass,
    confirm,
    loading,
    error,
    createdProduct,
    getaRedirect,
    formData
  } = values;

  const preload = () => {

        setValues({ ...values, formData: new FormData() });

  };

  useEffect(() => {
    preload();
  }, []);

  const onSubmit = event => {
    event.preventDefault();
    setValues({ ...values, error: "", loading: true });
    ChangePassword(user._id, token, { email, password }).then(data => {
      if (data.error) {
        setValues({ ...values, error: data.error });
      } else {
        setValues({
          ...values,
          oldpass: "",
          newpass: "",
          confirm: "",
          createdProduct:user.name1,
          loading: false,
        });
      }
    });
  };

  const handleChange = name => event => {
    const value = name === "photo" ? event.target.files[0] : event.target.value;
    formData.set(name, value);
    setValues({ ...values, [name]: value });
  };

  const successMessage = () => (
    <div
      className="alert alert-success mt-3"
      style={{ display: createdProduct ? "" : "none" }}
    >
      <h4>Thanks {createdProduct} Your SLot Confirmed successfully Wait for Our Response.</h4>
    </div>
  );


  const createProductForm = () => (
    <form>
      <br></br> <br></br>
      <span>Fill The Form To Update Password.</span>
      <br></br> <br></br>

      <div className="form-group">
        <input
          onChange={handleChange("oldpass")}
          name="oldpass"
          type="password"
          className="form-control"
          placeholder="Old Password"
          value={oldpass}
        />
      </div>
      <div className="form-group">
        <input
          onChange={handleChange("newpass")}
          name="newpass"
          type="password"
          className="form-control"
          placeholder="New Password"
          value={newpass}
        />
      </div>
      <div className="form-group">
        <input
          onChange={handleChange("confirm")}
          name="confirm"
          type="password"
          className="form-control"
          placeholder="confirm"
          value={confirm}
        />
      </div>

      <button
        type="submit"
        onClick={onSubmit}
        className="btn btn-outline-success mb-3"
      >
        Submit
      </button>
    </form>
  );

  return (
    <Base
      title="Update Password"
      description="Fill The Details To Change Your Password."
      className="container bg-info p-4"
    >
      <Link to="/register" className="btn btn-md btn-dark mb-3">
        Update Password
      </Link>
      <div className="row bg-dark text-white rounded">
        <div className="col-md-8 offset-md-2">
          {successMessage()}
          {createProductForm()}
        </div>
      </div>
    </Base>
  );
};

export default Password;
