import React from 'react'
import ImageHelper from './helper/ImageHelper';

    const Card = ({
      product
    }) => {
        return (
          <div className="card text-white bg-dark ">
            <div className="card-header lead">Product Name</div>
            <div className="card-body">
              <ImageHelper product={product}></ImageHelper>
              <p className="lead font-weight-normal text-wrap mt-3">
                Product Description
              </p>
              <p className="btn btn-dark rounded  btn-sm px-4">$ 5</p>
              <div className="row">
                <div className="col-12">
                  <button
                    onClick={() => {}}
                    className="btn btn-block btn-outline-white mt-2 mb-2"
                  >
                    Add to Cart
                  </button>
                </div>
                <div className="col-12">
                  <button
                    onClick={() => {}}
                    className="btn btn-block btn-outline-white mt-2 mb-2"
                  >
                    Remove from cart
                  </button>
                </div>
              </div>
            </div>
          </div>
        );
      };


export default Card;
