import React from 'react'
import {Link,withRouter} from 'react-router-dom';

const currentTab = (history,path) =>{
    if(history.location.pathname === path){
        return {color:'#FFFFFF'}
    }else{
        return {color:'#d1d1d1'}
    }
}

const Menu = ({history}) => {
    return (
        <div className='nav nav-tabs bg-dark'>
            <li className='nav-item'>
                <Link style={currentTab(history,'/')} 
                className='nav-link' 
                to='/'>
                    Home</Link>
            </li>
            <li className='nav-item'>
                <Link style={currentTab(history,'/Cart')} 
                className='nav-link' 
                to='/Cart'>
                    Cart</Link>
            </li>
            <li className='nav-item'>
                <Link style={currentTab(history,'/user/dashboard')} 
                className='nav-link' 
                to='/user/dashboard'>
                    DashBoard</Link>
            </li>
            <li className='nav-item'>
                <Link style={currentTab(history,'/admin/dashboard')}
                className='nav-link' 
                to='/admin/dashboard'>
                    A.DashBoard</Link>
            </li>
            <li className='nav-item'>
                <Link style={currentTab(history,'/singnup')}
                className='nav-link' 
                to='/singnup'>
                    SignUp</Link>
            </li>
            <li className='nav-item'>
                <Link style={currentTab(history,'/signin')}
                className='nav-link' 
                to='/signin'>
                    SignIn</Link>
            </li>
            <li className='nav-item'>
                <Link style={currentTab(history,'/singnout')}
                className='nav-link' 
                to='/singout'>
                    Signout</Link>
            </li>
        </div>
    );
};

export default withRouter(Menu);
