import React, { useState } from "react";
import Base from "../core/Base";
import { Link } from "react-router-dom";
import { ChangePassword } from "../auth/helper/index";

const Password = () => {
  const [values, setValues] = useState({
    oldpass: "",
    newpass: "",
    confirm: "",
    error: false,
    success: false
  });

  const { oldpass, newpass, confirm, error, success } = values;

  const handleChange = name => event => {
    setValues({ ...values, error: false, [name]: event.target.value });
  };

  const onSubmit = event => {
    event.preventDefault();
    setValues({ ...values, error: false });
    ChangePassword({ oldpass, newpass ,confirm })
      .then(data => {
        console.log(data);
        if (data.error) {
          setValues({ ...values, error: data.error, success: false });
        } else {
          setValues({
            ...values,
            oldpass: "",
            newpass: "",
            confirm: "",
            error: "",
            success: true
          });
        }
      })
      .catch(console.log("Error in Change Password"));
  };

  const signUpForm = () => {
    return (
      <div className="row">
        <div className="col-md-6 offset-sm-3 text-left">
          <form>
            <div className="form-group">
              <label className="text-light">Old Password</label>
              <input
                className="form-control"          
                type="password"
                onChange={handleChange("oldpass")}
                value={oldpass}
              />
            </div>
            <div className="form-group">
              <label className="text-light">New Password</label>
              <input
                className="form-control"
                onChange={handleChange("newpass")}
                type="password"
                value={newpass}
              />
            </div>
            <div className="form-group">
              <label className="text-light">Confirm</label>
              <input
                className="form-control"
                type="password"
                onChange={handleChange("confirm")}
                value={confirm}
              />
            </div>

            <button onClick={onSubmit} className="btn btn-primary btn-block">
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  };

  const successMessage = () => {
    return (
      <div className="row">
        <div className="col-md-6 offset-sm-3 text-left">
          <div
            className="alert alert-success"
            style={{ display: success ? "" : "none" }}
          >
            Password Updated
          </div>
        </div>
      </div>
    );
  };

  const errorMessage = () => {
    return (
      <div className="row">
        <div className="col-md-6 offset-sm-3 text-left">
          <div
            className="alert alert-danger"
            style={{ display: error ? "" : "none" }}
          >
            {error}
          </div>
        </div>
      </div>
    );
  };

  return (
    <Base title="Update Password" description="Fill The Details to Update Password">
      {successMessage()}
      {errorMessage()}
      {signUpForm()}
      <p>I was able to save Updated Password.Sorry sir Due to some technical errors i was unable to Update the Chnage Password Method I have Used db.ChangeUserPassword Method but still it has some minor Error To Convert Decrpyt Password . Except this all other functionlities are fine.</p>
    </Base>
  );
};

export default Password;
