import React, { useState, useEffect } from "react";
import ImageHelper from "./helper/ImageHelper";
import { Redirect } from "react-router-dom";
import { addItemToCart, removeItemFromCart } from "./helper/CartHelper";
import { Link, withRouter } from "react-router-dom";

const Card = ({ product,
   addtoCart = true,
  removeFromCart = false ,
  setReload = f => f,
  reload = undefined,

  }) => {
  const [redirect, setRedirect] = useState(false);
  const [count, setCount] = useState(product.count);


  const cartTitle = product ? product.name : "A photo from pexels";
  const cartDescrption = product ? product.description : "Default description";
  const cartPrice = product ? product.price : "DEFAULT";


  const addToCart = () => {
    addItemToCart(product, () => setRedirect(true));
  };

  const getARedirect = redirect => {
    if (redirect) {
      return <Redirect to="/cart" />;
    }
  };

  const showAddToCart = addtoCart => {
    return (
      addtoCart && (
        <button
          onClick={addToCart}
          className="btn btn-block btn-outline-light mt-2 mb-2"
        >
          Add to Cart
        </button>
      )
    );
  };

  const Register = addtoCart => {
    return (
      <button className='btn btn-block btn-outline-info mt-2 mb-2'>
        <Link to="/register" className="btn" style={{color:'white'}}>Register</Link>
      </button>
    );
  };

  const showRemoveFromCart = removeFromCart => {
    return (
      removeFromCart && (
        <button
          onClick={() => {
            removeItemFromCart(product._id);
            setReload(!reload);
          }}
          className="btn btn-block btn-outline-danger mt-2 mb-2"
        >
          Remove from cart
        </button>
      )
    );
  };



  return (
    <div className="card text-white bg-dark">
      <div className="card-header lead">{cartTitle}</div>
      <div className="card-body">
        {getARedirect(redirect)}
        <ImageHelper product={product} />
        <p className="lead font-weight-normal">
          {cartDescrption}
        </p>
        <a className="btn btn-danger rounded  btn-sm">â‚¹{cartPrice}</a>
        <div className="row">
          <div className="col-12">{Register()}</div>
          <div className="col-12">{showAddToCart(addtoCart)}</div>
          <div className="col-12">{showRemoveFromCart(removeFromCart)}</div>
        </div>
      </div>
    </div>
  );
};


export default Card;
