import React ,{useState,useEffect} from 'react'
import ImageHelper from './helper/ImageHelper';
import { addItemToCart } from './helper/CartHelper';

    const Card = ({
      product,
      addtoCart = true,
      removeFromCart = false
    }) => {

      const [redirect, setRedirect] = useState(false);


      const cardTitle = product ? product.name:'Product Title'
      const cardDescription = product ? product.description:'Product Description'
      const cardPrice = product ? product.price:'Product Image'

      const addToCart = () =>{
        addItemToCart(product,()=> setRedirect(true))
      }

      const getRedirect = (redirect) =>{
        if(redirect){
          return <redirect to='/cart'/>
        }
      }



      const showAddToCart = (addtoCart) =>{
        return(
          addtoCart && (
            <button
                    onClick={addToCart()}
                    className="btn btn-block btn-outline-light mt-2 mb-2"
                  >
                    Add to Cart
                  </button>
          )
        )
      }
      const showRemoveFromCart = (removeFromCart) =>{
        return (removeFromCart && (
          <button
                    onClick={() => {}}
                    className="btn btn-block btn-outline-warning mt-2 mb-2"
                  >
                    Remove from cart
                  </button>
        )
        )
      }
        return (
          <div className="card text-white bg-dark ">
            <div className="card-header lead">{cardTitle}</div>
            <div className="card-body">
              {getRedirect(redirect)}
              <ImageHelper product={product}></ImageHelper>
              <p className="lead font-weight-normal text-wrap mt-3">
                {cardDescription}
              </p>
              <p className="btn btn-info rounded  btn-sm px-4">$ {cardPrice}</p>
              <div className="row">
                <div className="col-12">
                  {showAddToCart(addtoCart)}
                </div>
                <div className="col-12">
                  {showRemoveFromCart(removeFromCart)}
                </div>
              </div>
            </div>
          </div>
        );
      };


export default Card;
