import React, { useState } from "react";
import Base from "../core/Base";
import { Link } from "react-router-dom";
import { signup } from "../auth/helper/index";

const Password = () => {
  const [values, setValues] = useState({
    first: "",
    last: "",
    school: "",
    address: "",
    city: "",
    email: "",
    mobile: "",
    password: "",
    error: false,
    success: false
  });

  const { first, last, school, address, city, email, mobile, password, error, success } = values;

  const handleChange = name => event => {
    setValues({ ...values, error: false, [name]: event.target.value });
  };

  const onSubmit = event => {
    event.preventDefault();
    setValues({ ...values, error: false });
    signup({ first, last ,school, address,city, email, mobile, password })
      .then(data => {
        console.log(data);
        if (data.error) {
          setValues({ ...values, error: data.error, success: false });
        } else {
          setValues({
            ...values,
            first: "",
            last: "",
            school: "",
            address: "",
            city: "",
            email: "",
            mobile: "",
            password: "",
            error: "",
            success: true
          });
        }
      })
      .catch(console.log("Error in signup"));
  };

  const signUpForm = () => {
    return (
      <div className="row">
        <div className="col-md-6 offset-sm-3 text-left">
          <form>
            <div className="form-group">
              <label className="text-light">Old Password</label>
              <input
                className="form-control"          
                type="password"
                onChange={handleChange("first")}
                type="text"
                value={first}
              />
            </div>
            <div className="form-group">
              <label className="text-light">New Password</label>
              <input
                className="form-control"
                onChange={handleChange("last")}
                type="text"
                type="password"
                value={last}
              />
            </div>
            <div className="form-group">
              <label className="text-light">Confirm</label>
              <input
                className="form-control"
                type="password"
                onChange={handleChange("school")}
                type="text"
                value={school}
              />
            </div>

            <button onClick={onSubmit} className="btn btn-primary btn-block">
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  };

  const successMessage = () => {
    return (
      <div className="row">
        <div className="col-md-6 offset-sm-3 text-left">
          <div
            className="alert alert-success"
            style={{ display: success ? "" : "none" }}
          >
            Password Updated
          </div>
        </div>
      </div>
    );
  };

  const errorMessage = () => {
    return (
      <div className="row">
        <div className="col-md-6 offset-sm-3 text-left">
          <div
            className="alert alert-danger"
            style={{ display: error ? "" : "none" }}
          >
            {error}
          </div>
        </div>
      </div>
    );
  };

  return (
    <Base title="Update Password" description="Fill The Details to Update Password">
      {successMessage()}
      {errorMessage()}
      {signUpForm()}
    </Base>
  );
};

export default Password;
