import React ,{useState,useEffect}from "react";
import "../styles.css";
import { API } from "../backend";
import Base from "./Base";
import Card from "./Card";
import { getProducts } from "./helper/coreapicalls";

export default function Home() {

    const [Products, setProducts] = useState([])  
    const [error, setError] = uEeState(false)


    const loadAllProducts = () =>{
      getProducts().then(data=>{
        if(data.error)
        {
          setError(data.error);
        }else{
          setProducts(data);
        }
      })
    }

    useEffect(() => {
      loadAllProducts()
    }, [])

  return (
    <Base title="Home Page" description="Welcome to the Tshirt Store">
      <div className="row">
        <div className="col-4">
          <Card></Card>
        </div>
        <div className="col-4">
        <Card></Card>
        </div>
        <div className="col-4">
        <Card></Card>
        </div>
        <div className="col-4">
        <Card></Card>
        </div>
        <div className="col-4">
        <Card></Card>
        </div>
        <div className="col-4">
        <Card></Card>
        </div>
      </div>
    </Base>
  );
}
