import { createPlayer } from '../core/helper/coreapicalls';
import React, { useState, useEffect } from "react";
import Base from "../core/Base";
import { Link } from "react-router-dom";
import { getCategories, createProduct } from "../admin/helper/adminapicall";
import { isAuthenticated } from "../auth/helper/index";

const Password = () => {
  // https://pubg-mobile-tournament.herokuapp.com/api
  const { user, token } = isAuthenticated();

  const [values, setValues] = useState({
    name1: "",
    name2: "",
    name3: "",
    name4: "",
    Transaction:"",
    number:"",
    photo: "",
    categories: [],
    category: "",
    loading: false,
    error: "",
    createdProduct: "",
    getaRedirect: false,
    formData: ""
  });

  const {
    name1,
    name2,
    name3,
    name4,
    Transaction,
    number,
    categories,
    category,
    loading,
    error,
    createdProduct,
    getaRedirect,
    formData
  } = values;

  const preload = () => {
        setValues({ ...values, formData: new FormData() });  
  };

  useEffect(() => {
    preload();
  }, []);

  const onSubmit = event => {
    event.preventDefault();
    setValues({ ...values, error: "", loading: true });
    createPlayer(user._id, token, formData).then(data => {
      if (data.error) {
        setValues({ ...values, error: data.error });
      } else {
        setValues({
          ...values,
          name1: "",
          name2: "",
          name3: "",
          name4: "",
          Transaction: "",
          number: "",
          photo: "",
          loading: false,
          createdProduct: data.name1
        });
      }
    });
  };

  const handleChange = name => event => {
    const value = name === "photo" ? event.target.files[0] : event.target.value;
    formData.set(name, value);
    setValues({ ...values, [name]: value });
  };

  const successMessage = () => (
    <div
      className="alert alert-success mt-3"
      style={{ display: createdProduct ? "" : "none" }}
    >
      <h4>Password Updated {createdProduct}</h4>
    </div>
  );


  const createProductForm = () => (
    <form>
      <br></br> <br></br>
      <span>Fill The Form.</span>
      <br></br> <br></br>

      <div className="form-group">
        <input
          onChange={handleChange("name1")}
          name="name1"
          type="password"
          className="form-control"
          placeholder="Old Password"
          value={name1}
        />
      </div>
      <div className="form-group">
        <input
          onChange={handleChange("name2")}
          name="name2"
          type="password"
          className="form-control"
          placeholder="New Password"
          value={name2}
        />
      </div>
      <div className="form-group">
        <input
          onChange={handleChange("name3")}
          name="name3"
          type="password"
          className="form-control"
          placeholder="Confirm Password"
          value={name3}
        />
      </div>


      <button
        type="submit"
        onClick={onSubmit}
        className="btn btn-outline-success mb-3"
      >
        Submit
      </button>
    </form>
  );

  return (
    <Base
      title="UPDATE PASSWORD"
      description="Fill The Details To Change Your Password."
      className="container bg-info p-4"
    >
      <Link to="/register" className="btn btn-md btn-dark mb-3">
        Update Password
      </Link>
      <div className="row bg-dark text-white rounded">
        <div className="col-md-8 offset-md-2">
          {successMessage()}
          {createProductForm()}
        </div>
      </div>
    </Base>
  );
};

export default Password;
